import java.lang.RuntimeException;public class S<T> implements Stack<T>{	final static int INITIAL_CAPACITY=1;	private Object[] v;	private int vSize;	public S()	{		makeEmpty();	}		@Override	public void push(T o)	{		if(vSize>=v.length)		{			Object[] newv = new Object[2*v.length];			System.arraycopy(v, 0, newv, 0, v.length);			v = newv;		}		v[vSize++] = o; 	}		@Override	public T top() throws EmptyStackException	{		if(isEmpty())		{			throw new EmptyStackException();		}				return (T)v[vSize-1];			}		@Override	public T pop() throws EmptyStackException	{		if(isEmpty())		{			throw new EmptyStackException();		}		T tmp = (T)v[vSize-1];		v[vSize-1] = null;		vSize--;		return tmp;	}		public boolean isEmpty()	{		return vSize<=0;	}	public void makeEmpty()	{		v = new Object[INITIAL_CAPACITY];		vSize=0;	}	public int size()	{		return vSize;	}		@Override	public String toString()	{		String output = "";		for(int i=0; i<vSize; i++)		{			output = output + v[i] + " ";		}		return output;	}}